import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend } from 'chart.js';

// Registering necessary Chart.js components
ChartJS.register(BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend);

const KeywordAnalysis = ({ keywords }) => {
  // Count the occurrences of each keyword
  const keywordCounts = keywords.reduce((acc, keyword) => {
    acc[keyword] = (acc[keyword] || 0) + 1;
    return acc;
  }, {});

  // Sort the keywords by frequency in descending order
  const sortedKeywords = Object.entries(keywordCounts).sort((a, b) => b[1] - a[1]);

  // Data for the chart
  const labels = sortedKeywords.map(([keyword]) => keyword);
  const data = sortedKeywords.map(([, count]) => count);

  // Chart data configuration
  const chartData = {
    labels: labels,
    datasets: [
      {
        label: 'Keyword Count',
        data: data,
        backgroundColor: 'rgba(75, 192, 192, 0.6)', // bar color
      },
    ],
  };

  // Chart options configuration
  const options = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      title: {
        display: true,
        text: 'Keyword Analysis',
      },
    },
  };

  return (
    <div>
      <h3>Keyword Analysis</h3>
      {/* Rendering the Bar chart */}
      <Bar data={chartData} options={options} />
    </div>
  );
};

export default KeywordAnalysis;
