import React, { useEffect, useState } from 'react';
import KeywordAnalysis from '../components/KeywordAnalysis';
import SentimentAnalysis from '../components/SentimentAnalysis';
import MediaAnalysisOverview from '../components/MediaAnalysisOverview';
import EntryTimeline from '../components/EntryTimeline';
import { useUser } from '../UserContext';

const Insights = () => {
  const { user } = useUser();
  const [entries, setEntries] = useState([]);
  const [keywords, setKeywords] = useState([]);
  const [mediaAnalysis, setMediaAnalysis] = useState([]);

  useEffect(() => {
    const fetchEntries = async () => {
      if (!user) {
        // Handle the case where user is null or undefined
        console.log("User data is not available");
        return;
      }

      try {
        const response = await fetch(`http://localhost:3001/journal-entries/${user.username}`);
        if (response.ok) {
          const data = await response.json();
          setEntries(data);
          // Aggregate keywords and media analysis results
          const allKeywords = data.flatMap(entry => entry.metadata.keywords);
          setKeywords(allKeywords);
          const allMediaAnalysis = data.map(entry => entry.mediaAnalysis || { labels: [], texts: [] });
          setMediaAnalysis(allMediaAnalysis);
        }
      } catch (error) {
        console.error('Error fetching journal entries:', error);
      }
    };

    fetchEntries();
  }, [user]);

  // Return loading or a placeholder if user is not available
  if (!user) {
    return <div>Loading user data...</div>; // You can replace this with a more user-friendly message or spinner
  }

  return (
    <div>
      <h1>Insights</h1>
      <KeywordAnalysis keywords={keywords} />
      <SentimentAnalysis entries={entries} />
      <MediaAnalysisOverview mediaAnalysis={mediaAnalysis} />
      <EntryTimeline entries={entries} />
    </div>
  );
};

export default Insights;
