import React from 'react';

const MetadataDisplay = ({ metadata, onMetadataChange }) => {
  const { startTime, endTime, duration, date, keywords, names } = metadata;

  const formatTime = (time) => {
    return time ? new Date(time).toLocaleTimeString() : 'N/A';
  };

  const formatDate = (time) => {
    return time ? new Date(time).toLocaleDateString() : 'N/A';
  };

  const formatDuration = (duration) => {
    if (!duration) return 'N/A';
    const seconds = Math.floor(duration / 1000);
    return `${seconds} seconds`;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    onMetadataChange({ ...metadata, [name]: value });
  };

  const fetchKeywordsFromSpeechToText = async () => {
    try {
      const response = await fetch('http://localhost:3001/analyze-speech', { method: 'POST' });
      const data = await response.json();
      onMetadataChange({ ...metadata, keywords: data.keywords });
    } catch (error) {
      console.error('Error fetching keywords from speech-to-text:', error);
    }
  };

  const fetchKeywordsFromMedia = async () => {
    try {
      const response = await fetch('http://localhost:3001/analyze-media', { method: 'POST' });
      const data = await response.json();
      onMetadataChange({ ...metadata, keywords: data.keywords, names: data.names });
    } catch (error) {
      console.error('Error fetching keywords from media:', error);
    }
  };

  return (
    <div>
      <h2>Recording Metadata</h2>
      <p>
        <strong>Date:</strong>
        <input
          type="date"
          name="date"
          value={date ? new Date(date).toISOString().split('T')[0] : ''}
          onChange={handleInputChange}
        />
      </p>
      <p>
        <strong>Start Time:</strong>
        <input
          type="text"
          name="startTime"
          value={startTime ? formatTime(startTime) : ''}
          onChange={handleInputChange}
        />
      </p>
      <p>
        <strong>End Time:</strong>
        <input
          type="text"
          name="endTime"
          value={endTime ? formatTime(endTime) : ''}
          onChange={handleInputChange}
        />
      </p>
      <p>
        <strong>Duration:</strong>
        <input
          type="text"
          name="duration"
          value={duration ? formatDuration(duration) : ''}
          onChange={handleInputChange}
        />
      </p>
      <p>
        <strong>Names:</strong>
        <input
          type="text"
          name="names"
          value={names && names.length > 0 ? names.join(', ') : ''}
          onChange={(e) => handleInputChange({ target: { name: 'names', value: e.target.value.split(',').map(name => name.trim()) } })}
        />
      </p>
      <p>
        <strong>Keywords:</strong>
        <input
          type="text"
          name="keywords"
          value={keywords && keywords.length > 0 ? keywords.join(', ') : ''}
          onChange={(e) => handleInputChange({ target: { name: 'keywords', value: e.target.value.split(',').map(kw => kw.trim()) } })}
        />
      </p>
      <button onClick={fetchKeywordsFromSpeechToText}>Fetch Keywords from Speech-to-Text</button>
      <button onClick={fetchKeywordsFromMedia}>Fetch Keywords and Names from Media Analysis</button>
    </div>
  );
};

export default MetadataDisplay;
