import React, { useState, useEffect } from 'react';
import MediaUpload from './MediaUpload';
import SpeechToText from './SpeechToText';
import { useUser } from '../UserContext';

const JournalEntry = () => {
  const { user } = useUser(); // Access the user from context
  const [text, setText] = useState('');
  const [metadata, setMetadata] = useState({
    startTime: null,
    endTime: null,
    duration: null,
    date: '', // Initialize date as an empty string
    keywords: [],
    names: [],
  });
  const [selectedMedia, setSelectedMedia] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState('');

  // Populate the current date when the component loads
  useEffect(() => {
    if (!metadata.date) {
      const currentDate = new Date().toISOString().split('T')[0]; // Format as YYYY-MM-DD
      setMetadata((prevMetadata) => ({
        ...prevMetadata,
        date: currentDate,
      }));
    }
  }, []); // Empty dependency array ensures this runs only once

  const handleSubmit = async (event) => {
    event.preventDefault();
  
    const currentTime = new Date();
  
    // Ensure metadata is correctly populated
    const updatedMetadata = {
      ...metadata,
      startTime: metadata.startTime || currentTime.toISOString(),
      endTime: metadata.endTime || currentTime.toISOString(),
      keywords: metadata.keywords.length > 0 ? metadata.keywords : ['default'],
      names: metadata.names.length > 0 ? metadata.names : ['default'],
    };
  
    const journalEntryData = {
      text: text || 'No text provided', // Ensure text is not empty
      metadata: JSON.stringify(updatedMetadata),
      user: 'brad123', // Ensure user info is included
    };
  
    console.log('Sending data to server:', journalEntryData); // Log the data
  
    try {
      const response = await fetch('http://localhost:3001/journal-entries', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(journalEntryData),
      });
  
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
  
      const result = await response.json();
      console.log('Response from server:', result);
    } catch (error) {
      console.error('Error submitting journal entry:', error);
    }
  };
  

  return (
    <div>
      <h2>Create Journal Entry</h2>
      <SpeechToText

      />
      <MediaUpload onMediaSelect={setSelectedMedia} />
      <button onClick={handleSubmit} disabled={isSubmitting}>
        {isSubmitting ? 'Submitting...' : 'Submit'}
      </button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default JournalEntry;
