import React, { useState, useEffect } from 'react';
import SpeechToText from './SpeechToText';
import { useUser } from '../UserContext';

const JournalEntry = () => {
  const { user } = useUser();
  const [metadata, setMetadata] = useState({
    startTime: null,
    endTime: null,
    duration: null,
    date: '',
    keywords: [],
    names: [],
    text: '', // Ensure text is included in metadata
  });

  const handleMetadataChange = (updatedMetadata) => {
    setMetadata(updatedMetadata);
  };

  const formatTime = (time) => {
    return time ? new Date(time).toLocaleTimeString() : 'N/A';
  };

  const formatDate = (time) => {
    return time ? new Date(time).toLocaleDateString() : 'N/A';
  };

  useEffect(() => {
    if (metadata.endTime) {
      const currentDate = new Date(metadata.endTime).toISOString().split('T')[0]; // YYYY-MM-DD format
      setMetadata((prevMetadata) => ({
        ...prevMetadata,
        date: currentDate,
      }));
    }
  }, [metadata.endTime]);

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:5000/journalentries', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user: user.username,
          text: metadata.text,
          metadata: metadata,
        }),
      });
      if (response.ok) {
        console.log('Journal entry saved successfully');
      } else {
        console.error('Failed to save journal entry');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Create Journal Entry</h2>
      <SpeechToText onMetadataChange={handleMetadataChange} />
      <div style={{ textAlign: 'left', marginTop: '20px' }}>
        <h3>Recording Metadata</h3>
        <p>
          <strong>Date:</strong> {metadata.date}<br />
          <strong>Start Time:</strong> {formatTime(metadata.startTime)}<br />
          <strong>End Time:</strong> {formatTime(metadata.endTime)}<br />
          <strong>Duration:</strong> {metadata.duration && `${Math.floor(metadata.duration / 1000)} seconds`}<br />
          <strong>Text:</strong> {metadata.text}<br />
          <strong>Names:</strong> {metadata.names.join(', ')}<br />
          <strong>Keywords:</strong> {metadata.keywords.join(', ')}
        </p>
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default JournalEntry;
