import React from 'react';

const EntryTimeline = ({ entries }) => {
  // Mock data with entries
  const mockEntries = [
    { metadata: { date: '2025-01-10T08:30:00Z' }, text: 'This is the first entry of the day, in the morning.' },
    { metadata: { date: '2025-01-10T13:45:00Z' }, text: 'Lunch meeting with the team, afternoon thoughts.' },
    { metadata: { date: '2025-01-10T19:10:00Z' }, text: 'Reflecting on the day’s progress, evening thoughts.' },
    { metadata: { date: '2025-01-11T09:00:00Z' }, text: 'Morning jog, feeling energized!' },
    { metadata: { date: '2025-01-11T14:20:00Z' }, text: 'Afternoon project update with the colleagues.' },
    { metadata: { date: '2025-01-11T20:30:00Z' }, text: 'Late-night reflection on the goals for the next week.' },
    { metadata: { date: '2025-01-12T07:00:00Z' }, text: 'Early morning brainstorming session.' },
    { metadata: { date: '2025-01-12T12:30:00Z' }, text: 'Midday meeting to discuss the upcoming project.' },
    { metadata: { date: '2025-01-12T18:00:00Z' }, text: 'Winding down, reviewing the work done today.' },
    { metadata: { date: '2025-01-13T08:15:00Z' }, text: 'Morning coffee and catching up on emails.' },
    { metadata: { date: '2025-01-13T14:45:00Z' }, text: 'Afternoon walk to clear my head.' },
    { metadata: { date: '2025-01-13T19:30:00Z' }, text: 'Evening relaxation, preparing for tomorrow’s tasks.' },
  ];

  // Sort entries by date
  const sortedEntries = mockEntries.sort((a, b) => new Date(a.metadata.date) - new Date(b.metadata.date));

  // Function to categorize entries into time blocks (Morning, Afternoon, Evening)
  const categorizeTime = (date) => {
    const hour = new Date(date).getHours();
    if (hour < 12) return 'Morning';
    if (hour < 18) return 'Afternoon';
    return 'Evening';
  };

  // Create a set of unique dates
  const uniqueDates = [...new Set(sortedEntries.map((entry) => new Date(entry.metadata.date).toLocaleDateString()))];

  return (
    <div style={{ marginTop: '30px' }}>
      <h3>Entry Timeline</h3>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Date</th>
            <th>Morning</th>
            <th>Afternoon</th>
            <th>Evening</th>
          </tr>
        </thead>
        <tbody>
          {uniqueDates.map((date) => (
            <tr key={date}>
              <td>{date}</td>
              <td>
                {sortedEntries
                  .filter(
                    (entry) =>
                      new Date(entry.metadata.date).toLocaleDateString() === date &&
                      categorizeTime(entry.metadata.date) === 'Morning'
                  )
                  .map((entry, index) => (
                    <div key={index} style={{ backgroundColor: '#d3f9d8', padding: '5px', margin: '5px 0' }}>
                      {entry.text}
                    </div>
                  ))}
              </td>
              <td>
                {sortedEntries
                  .filter(
                    (entry) =>
                      new Date(entry.metadata.date).toLocaleDateString() === date &&
                      categorizeTime(entry.metadata.date) === 'Afternoon'
                  )
                  .map((entry, index) => (
                    <div key={index} style={{ backgroundColor: '#ffe9b3', padding: '5px', margin: '5px 0' }}>
                      {entry.text}
                    </div>
                  ))}
              </td>
              <td>
                {sortedEntries
                  .filter(
                    (entry) =>
                      new Date(entry.metadata.date).toLocaleDateString() === date &&
                      categorizeTime(entry.metadata.date) === 'Evening'
                  )
                  .map((entry, index) => (
                    <div key={index} style={{ backgroundColor: '#f8d7da', padding: '5px', margin: '5px 0' }}>
                      {entry.text}
                    </div>
                  ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EntryTimeline;
