import React, { useEffect, useState } from 'react';
import KeywordAnalysis from '../components/KeywordAnalysis';
import SentimentAnalysis from '../components/SentimentAnalysis';
import MediaAnalysisOverview from '../components/MediaAnalysisOverview';
import EntryTimeline from '../components/EntryTimeline';
import { useUser } from '../UserContext';

const Insights = () => {
  const { user } = useUser();
  const [entries, setEntries] = useState([]);
  const [keywords, setKeywords] = useState([]);
  const [mediaAnalysis, setMediaAnalysis] = useState([]);

  // Default data when the user is not signed in
  const defaultEntries = [
    {
      text: "This is an example journal entry with some random thoughts about the day.",
      metadata: { keywords: ["example", "day", "thoughts"] },
      mediaAnalysis: { labels: ["Positive", "Neutral"], texts: ["Great", "Okay"] },
    },
    {
      text: "Another entry that is a bit more negative, some frustrations here.",
      metadata: { keywords: ["negative", "frustration"] },
      mediaAnalysis: { labels: ["Negative", "Neutral"], texts: ["Sad", "Indifferent"] },
    },
  ];

  const defaultKeywords = ["example", "day", "thoughts", "negative", "frustration"];
  const defaultMediaAnalysis = [
    { labels: ["Positive", "Neutral"], texts: ["Great", "Okay"] },
    { labels: ["Negative", "Neutral"], texts: ["Sad", "Indifferent"] },
  ];

  // If the user is signed in, fetch their entries, else use default data
  useEffect(() => {
    if (user) {
      const fetchEntries = async () => {
        try {
          const response = await fetch(`http://localhost:3001/journal-entries/${user.username}`);
          if (response.ok) {
            const data = await response.json();
            setEntries(data);
            // Aggregate keywords and media analysis results
            const allKeywords = data.flatMap(entry => entry.metadata.keywords);
            setKeywords(allKeywords);
            const allMediaAnalysis = data.map(entry => entry.mediaAnalysis || { labels: [], texts: [] });
            setMediaAnalysis(allMediaAnalysis);
          }
        } catch (error) {
          console.error('Error fetching journal entries:', error);
        }
      };

      fetchEntries();
    } else {
      // Use default data when the user is not signed in
      setEntries(defaultEntries);
      setKeywords(defaultKeywords);
      setMediaAnalysis(defaultMediaAnalysis);
    }
  }, [user]);

  return (
    <div>
      <h1>Insights</h1>
      <KeywordAnalysis keywords={keywords} />
      <SentimentAnalysis entries={entries} />
      <MediaAnalysisOverview mediaAnalysis={mediaAnalysis} />
      <EntryTimeline entries={entries} />
      {!user && (
        <div>
          <h4>Your data will look something like this:</h4>
          <p>Once you're signed in and start adding journal entries, you'll see insights based on your data here.</p>
        </div>
      )}
    </div>
  );
};

export default Insights;
