import React, { useState } from 'react';
import MediaUpload from './MediaUpload';
import SpeechToText from './SpeechToText';
import { useUser } from '../UserContext';

const JournalEntry = () => {
  const { user } = useUser(); // Access the user from context
  const [text, setText] = useState('');
  const [metadata, setMetadata] = useState({
    startTime: null,
    endTime: null,
    duration: null,
    date: null,
    keywords: [],
    names: [],
  });
  const [selectedMedia, setSelectedMedia] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState('');


  useEffect(() => {
    const today = new Date().toISOString().split('T')[0]; // Get date in YYYY-MM-DD format
    setMetadata((prevMetadata) => ({
      ...prevMetadata,
      date: today,
    }));
  }, []);

  const handleSubmit = async () => {
    if (!user || !user.username) {
      setMessage('User username is missing. Please log in again.');
      return;
    }

    setIsSubmitting(true);
    setMessage('');

    try {
      const formData = new FormData();
      formData.append('text', text);
      formData.append('metadata', JSON.stringify(metadata));
      selectedMedia.forEach((file) => formData.append('media', file));
      formData.append('user', user.username); // Use the dynamically stored username instead of user.id

      const response = await fetch('http://localhost:3001/journal-entries', {
        method: 'POST',
        body: formData,
        
      });

      if (!response.ok) throw new Error('Failed to submit journal entry');

      const data = await response.json();
      setMessage('Journal entry submitted successfully!');
      console.log('Submission response:', data);
    } catch (error) {
      console.error('Error submitting journal entry:', error.message);
      setMessage('An error occurred.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <h2>Create Journal Entry</h2>
      <SpeechToText onTextChange={(text, meta) => {
        setText(text);
        setMetadata(meta);
      }} />
      <MediaUpload onMediaSelect={setSelectedMedia} />
      <button onClick={handleSubmit} disabled={isSubmitting}>
        {isSubmitting ? 'Submitting...' : 'Submit'}
      </button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default JournalEntry;
