import React, { useEffect, useState } from 'react';
import { useUser } from '../UserContext';
import JournalEntryTable from '../components/JournalEntryTable';

const Dashboard = () => {
  const { user } = useUser(); // Get the current user from context
  const [journalEntries, setJournalEntries] = useState([]);

  useEffect(() => {
    const fetchJournalEntries = async () => {
      if (user && user.id) {
        try {
          const response = await fetch(`http://localhost:3001/journal-entries/${user.id}`);
          if (!response.ok) {
            throw new Error('Failed to fetch journal entries');
          }
          const data = await response.json();
          console.log('Journal Entries from Backend:', data); // Log the journal entries data
          setJournalEntries(data);
        } catch (error) {
          console.error(error.message);
        }
      }
    };
  
    fetchJournalEntries();
  }, [user]);
   // Add 'user' as a dependency to ensure the effect re-runs when 'user' changes

  return (
    <div>
      <JournalEntryTable journalEntries={journalEntries} />
    </div>
  );
};

export default Dashboard;
