import React, { useState } from 'react';

const MediaUpload = ({ onMediaSelect }) => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      onMediaSelect(file); // Notify parent component of selected file
    }
  };

  return (
    <div style={{ margin: '20px 0' }}>
      <h2>Upload Media</h2>
      <input type="file" accept="image/*,audio/*,video/*" onChange={handleFileChange} />
      {selectedFile && (
        <div style={{ marginTop: '10px' }}>
          <p>Selected File: {selectedFile.name}</p>
          {selectedFile.type.startsWith('image/') && <img src={URL.createObjectURL(selectedFile)} alt="Selected" style={{ maxWidth: '200px', maxHeight: '200px' }} />}
          {selectedFile.type.startsWith('audio/') && <audio controls src={URL.createObjectURL(selectedFile)} />}
          {selectedFile.type.startsWith('video/') && <video controls src={URL.createObjectURL(selectedFile)} style={{ maxWidth: '200px', maxHeight: '200px' }} />}
        </div>
      )}
    </div>
  );
};

export default MediaUpload;
