import React, { useState, useEffect } from 'react';
import SpeechToText from './SpeechToText';
import MediaUpload from './MediaUpload';
import { useUser } from '../UserContext';

const JournalEntry = () => {
  const { user } = useUser();
  const [metadata, setMetadata] = useState({
    startTime: null,
    endTime: null,
    duration: null,
    date: '',
    keywords: [],
    names: [],
    text: '', // Ensure text is included in metadata
  });

  const handleMetadataChange = (updatedMetadata) => {
    console.log('Metadata received from SpeechToText:', updatedMetadata);

    // Format the date field explicitly
    const formattedMetadata = {
      ...updatedMetadata,
      date: updatedMetadata.date ? new Date(updatedMetadata.date).toISOString().split('T')[0] : '',
    };

    setMetadata(formattedMetadata);
  };

  const handleMediaSelect = (files) => {
    console.log('Selected files:', files);
    // You can perform any additional processing with the selected files here
  };

  const formatTime = (time) => {
    return time ? new Date(time).toLocaleTimeString() : 'N/A';
  };

  const formatDate = (time) => {
    return time ? new Date(time).toLocaleDateString() : 'N/A';
  };

  useEffect(() => {
    if (metadata.endTime) {
      const currentDate = new Date(metadata.endTime).toISOString().split('T')[0]; // YYYY-MM-DD format
      setMetadata((prevMetadata) => ({
        ...prevMetadata,
        date: currentDate,
      }));
    }
  }, [metadata.endTime]);

  const handleSubmit = async () => {
    const formattedMetadata = {
      ...metadata,
      date: metadata.date ? new Date(metadata.date).toISOString().split('T')[0] : '',
      startTime: metadata.startTime ? new Date(metadata.startTime).toISOString() : null,
      endTime: metadata.endTime ? new Date(metadata.endTime).toISOString() : null,
    };
  
    try {
      const response = await fetch('http://localhost:3001/journal-entries', { // Update the endpoint here
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user: user.username,
          text: metadata.text,
          metadata: JSON.stringify(formattedMetadata), // Stringify metadata
        }),
      });
      if (response.ok) {
        console.log('Journal entry saved successfully');
      } else {
        console.error('Failed to save journal entry');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };
  
  return (
    <div>
      <SpeechToText onMetadataChange={handleMetadataChange} />
      <MediaUpload onMediaSelect={handleMediaSelect} />
      <div style={{ textAlign: 'left', marginTop: '20px' }}>
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default JournalEntry;
