import React, { useEffect, useState } from 'react';
import KeywordAnalysis from '../components/KeywordAnalysis';
import SentimentAnalysis from '../components/SentimentAnalysis';
import EntryTimelineWithHeatmaps from '../components/EntryTimeline';
import axios from 'axios';
import '@fortawesome/fontawesome-free/css/all.min.css';

import { useUser } from '../UserContext';
import '../styles/Insights.css';

const Insights = () => {
  const { user } = useUser();
  const [entries, setEntries] = useState([]);
  const [keywords, setKeywords] = useState([]);
  const [mediaAnalysis, setMediaAnalysis] = useState([]);
  const [summary, setSummary] = useState('');
  const [mentalHealthStrategy, setMentalHealthStrategy] = useState('');
  const [isStrategyVisible, setIsStrategyVisible] = useState(false); // State for toggling strategy visibility

  const API_URL = 'http://localhost:3001'; // Ensure this is correct for your backend

  const defaultEntries = [
    {
      text: "Had a fantastic day at work! Got a promotion, feeling on top of the world. Excited for the future!",
      metadata: { keywords: ['work', 'promotion', 'excited', 'future'] },
      mediaAnalysis: { labels: ['Negative', 'Neutral'], texts: ['Overwhelmed', 'Frustrated'] },
    },
    {
      text: "It's been a tough week. Struggling with personal issues, feeling a bit lost and overwhelmed.",
      metadata: { keywords: ['tough', 'personal', 'struggling', 'overwhelmed'] },
      mediaAnalysis: { labels: ['Negative', 'Neutral'], texts: ['Frustrated', 'Stressed'] },
    },
    {
      text: "Work was okay today. Not much happened, just the usual. Nothing too exciting or frustrating.",
      metadata: { keywords: ['work', 'usual', 'okay', 'average'] },
      mediaAnalysis: { labels: ['Negative', 'Neutral'], texts: ['Indifferent', 'Stressed'] },
    },
  ];

  useEffect(() => {
    if (user) {
      const fetchEntries = async () => {
        try {
          const response = await fetch(`${API_URL}/journal-entries/${user.username}`);
          if (response.ok) {
            const data = await response.json();
            setEntries(data);
            aggregateData(data);
          }
        } catch (error) {
          console.error('Error fetching journal entries:', error);
        }
      };

      fetchEntries();
    } else {
      setEntries(defaultEntries);
      aggregateData(defaultEntries);
    }
  }, [user]);

  const aggregateData = (data) => {
    const allKeywords = data.flatMap((entry) => entry.metadata.keywords);
    const allMediaAnalysis = data.map((entry) => entry.mediaAnalysis || { labels: [], texts: [] });

    setKeywords(allKeywords);
    setMediaAnalysis(allMediaAnalysis);

    const sentiments = allMediaAnalysis.flatMap((analysis) => analysis.labels);
    const positiveCount = sentiments.filter((label) => label === 'Positive').length;
    const negativeCount = sentiments.filter((label) => label === 'Negative').length;
    const neutralCount = sentiments.filter((label) => label === 'Neutral').length;

    let sentimentSummary = 'Mixed emotions were observed.';
    if (positiveCount > negativeCount && positiveCount > neutralCount) {
      sentimentSummary = 'Most entries indicate positive emotions.';
    } else if (negativeCount > positiveCount && negativeCount > neutralCount) {
      sentimentSummary = 'Many entries indicate negative emotions.';
    } else if (neutralCount > positiveCount && neutralCount > negativeCount) {
      sentimentSummary = 'The entries are mostly neutral.';
    }

    setSummary(sentimentSummary);

    fetchMentalHealthStrategy(sentimentSummary.toLowerCase());
  };

  const fetchMentalHealthStrategy = async (sentimentSummary) => {
    try {
      const response = await axios.post(`${API_URL}/api/strategies`, { sentiment: sentimentSummary });
      const strategyData = response.data.strategy;
  
      console.log('Full Strategy Data:', strategyData); // Log the full response
  
      if (strategyData) {
        // Assuming the strategy includes a structure with both "Strategy" and "Actionable Steps"
        const [strategyText, actionableStepsText] = strategyData.split('Actionable Steps:');
  
        const actionableSteps = actionableStepsText
          ? actionableStepsText.split('\n').map(step => step.trim()).filter(step => step) // Clean and separate steps
          : [];
  
        setMentalHealthStrategy({
          description: strategyText.trim(),
          steps: actionableSteps
        });
      } else {
        console.error('No strategy data available.');
      }
    } catch (error) {
      console.error('Error fetching strategy:', error);
    }
  };
  

  return (
    <div className="insights-container">
      <h1 className="insights-title">Insights</h1>

      {!user && (
        <div className="card-container">
          <h4>Your data will look something like this:</h4>
          <p>Sign in to see personalized insights based on your entries.</p>
        </div>
      )}
  {mentalHealthStrategy && (
  <div className="card-container">
    <h2>Suggested Mental Health Strategy</h2>

    {/* Table for Strategy */}
    <table className="strategy-table">
      <tbody>
        <tr>
          <td><strong>Strategy:</strong></td>
          <td>{mentalHealthStrategy.description}</td>
        </tr>
      </tbody>
    </table>

    {/* Table for Actionable Steps */}
    {mentalHealthStrategy.steps && (
      <div>
        <h3>Actionable Steps</h3>
        <table className="steps-table">
          <tbody>
            {mentalHealthStrategy.steps.map((step, index) => (
              <tr key={index}>
                <td>{index + 1}. {step}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )}
  </div>
)}


      {/* Keyword Analysis */}
      <div className="card-container">
        <KeywordAnalysis keywords={keywords} />
      </div>

      {/* Sentiment Analysis */}
      <div className="card-container">
        <SentimentAnalysis entries={entries} />
      </div>

      {/* Timeline Analysis */}
      <div className="card-container">
        <EntryTimelineWithHeatmaps entries={entries} />
      </div>
    </div>
  );
};

export default Insights;
