import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, Title, Tooltip, Legend, BarElement } from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  Title,
  Tooltip,
  Legend,
  BarElement
);

const categorizeTime = (timestamp) => {
  const hour = new Date(timestamp).getHours();
  return hour < 6
    ? '00-06'
    : hour < 12
    ? '06-12'
    : hour < 18
    ? '12-18'
    : '18-24';
};

const generateSentimentData = (entries) => {
  return entries.map(() => Math.floor(Math.random() * 3) - 1); // Generate sentiment data (-1, 0, 1)
};

const EntryTimelineWithHeatmaps = ({ entries }) => {
  const categorizedEntries = entries.map((entry) => categorizeTime(entry.metadata.date));

  // Generate mock sentiment data
  const sentimentData = generateSentimentData(entries);

  // Prepare heatmap data for entry times
  const entryTimeHeatmapData = categorizedEntries.reduce((acc, timeSlot) => {
    acc[timeSlot] = (acc[timeSlot] || 0) + 1;
    return acc;
  }, {});

  // Prepare heatmap data for sentiment
  const sentimentHeatmapData = sentimentData.reduce((acc, sentiment) => {
    const category = sentiment === 1 ? 'Positive' : sentiment === 0 ? 'Neutral' : 'Negative';
    acc[category] = (acc[category] || 0) + 1;
    return acc;
  }, {});

  // Prepare chart data for Entry Time heatmap (using a Bar chart)
  const entryTimeChartData = {
    labels: ['00-06', '06-12', '12-18', '18-24'],
    datasets: [
      {
        label: 'Entries per Time Slot',
        data: [
          entryTimeHeatmapData['00-06'] || 0,
          entryTimeHeatmapData['06-12'] || 0,
          entryTimeHeatmapData['12-18'] || 0,
          entryTimeHeatmapData['18-24'] || 0
        ],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  // Prepare chart data for Sentiment heatmap (using a Bar chart)
  const sentimentChartData = {
    labels: ['Positive', 'Neutral', 'Negative'],
    datasets: [
      {
        label: 'Sentiment Count',
        data: [
          sentimentHeatmapData['Positive'] || 0,
          sentimentHeatmapData['Neutral'] || 0,
          sentimentHeatmapData['Negative'] || 0
        ],
        backgroundColor: [
          'rgba(75, 192, 192, 0.6)', 
          'rgba(255, 159, 64, 0.6)', 
          'rgba(255, 99, 132, 0.6)'
        ],
        borderColor: [
          'rgba(75, 192, 192, 1)', 
          'rgba(255, 159, 64, 1)', 
          'rgba(255, 99, 132, 1)'
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div>
      <h3>Entry Timeline Heatmap</h3>
      <Bar data={entryTimeChartData} options={{ responsive: true, plugins: { title: { display: true, text: 'Entries by Time Slot' } } }} />

      <h3>Sentiment Heatmap</h3>
      <Bar data={sentimentChartData} options={{ responsive: true, plugins: { title: { display: true, text: 'Sentiment Distribution' } } }} />
    </div>
  );
};

export default EntryTimelineWithHeatmaps;
