import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, Title, Tooltip, Legend, BarElement } from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  Title,
  Tooltip,
  Legend,
  BarElement
);

const categorizeTime = (timestamp) => {
  const hour = new Date(timestamp).getHours();
  if (hour >= 6 && hour < 12) return 'Morning';   // 06:00 - 11:59
  if (hour >= 12 && hour < 18) return 'Afternoon'; // 12:00 - 17:59
  return 'Night';                                 // 18:00 - 05:59
};

const generateSentimentData = (entries) => {
  return entries.map(() => Math.floor(Math.random() * 3) - 1); // Generate sentiment data (-1, 0, 1)
};

const EntryTimelineWithHeatmaps = ({ entries }) => {
  const categorizedEntries = entries.map((entry) => categorizeTime(entry.metadata.date));

  // Generate mock sentiment data
  const sentimentData = generateSentimentData(entries);

  // Prepare heatmap data for entry times
  const entryTimeHeatmapData = categorizedEntries.reduce((acc, timeSlot) => {
    acc[timeSlot] = (acc[timeSlot] || 0) + 1;
    return acc;
  }, {});

  // Prepare heatmap data for sentiment by time slot
  const sentimentHeatmapData = sentimentData.reduce((acc, sentiment, index) => {
    const timeSlot = categorizedEntries[index];
    const category = sentiment === 1 ? 'Positive' : sentiment === 0 ? 'Neutral' : 'Negative';
    acc[timeSlot] = acc[timeSlot] || { Positive: 0, Neutral: 0, Negative: 0 };
    acc[timeSlot][category] += 1;
    return acc;
  }, {});

  // Prepare chart data for Entry Time heatmap (using a Bar chart)
  const entryTimeChartData = {
    labels: ['Morning', 'Afternoon', 'Night'],
    datasets: [
      {
        label: 'Entries per Time Slot',
        data: [
          entryTimeHeatmapData['Morning'] || 0,
          entryTimeHeatmapData['Afternoon'] || 0,
          entryTimeHeatmapData['Night'] || 0
        ],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  // Prepare chart data for Sentiment heatmap by time slot (using a Bar chart)
  const sentimentChartData = {
    labels: ['Morning', 'Afternoon', 'Night'],
    datasets: [
      {
        label: 'Positive Sentiment',
        data: [
          sentimentHeatmapData['Morning']?.Positive || 0,
          sentimentHeatmapData['Afternoon']?.Positive || 0,
          sentimentHeatmapData['Night']?.Positive || 0
        ],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
      {
        label: 'Neutral Sentiment',
        data: [
          sentimentHeatmapData['Morning']?.Neutral || 0,
          sentimentHeatmapData['Afternoon']?.Neutral || 0,
          sentimentHeatmapData['Night']?.Neutral || 0
        ],
        backgroundColor: 'rgba(255, 159, 64, 0.6)',
        borderColor: 'rgba(255, 159, 64, 1)',
        borderWidth: 1,
      },
      {
        label: 'Negative Sentiment',
        data: [
          sentimentHeatmapData['Morning']?.Negative || 0,
          sentimentHeatmapData['Afternoon']?.Negative || 0,
          sentimentHeatmapData['Night']?.Negative || 0
        ],
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  return (
    <div>
      <h3>Entry Timeline Heatmap</h3>
      <Bar data={entryTimeChartData} options={{ responsive: true, plugins: { title: { display: true, text: 'Entries by Time of Day' } } }} />

      <h3>Sentiment Heatmap by Time of Day</h3>
      <Bar data={sentimentChartData} options={{ responsive: true, plugins: { title: { display: true, text: 'Sentiment Distribution by Time of Day' } } }} />
    </div>
  );
};

export default EntryTimelineWithHeatmaps;
