import React, { useState, useEffect } from 'react';
import SpeechToText from './SpeechToText';
import MetadataDisplay from './MetadataDisplay';
import { useUser } from '../UserContext';

const JournalEntry = () => {
  const { user } = useUser();
  const [metadata, setMetadata] = useState({
    startTime: null,
    endTime: null,
    duration: null,
    date: new Date().toISOString().split('T')[0], // Set default date to the current date
    keywords: [],
    names: [],
    text: '', // Ensure text is included in metadata
  });

  const handleMetadataChange = (updatedMetadata) => {
    console.log('Metadata received from SpeechToText:', updatedMetadata);
    setMetadata(updatedMetadata);
  };

  const handleMetadataUpdate = (updatedFields) => {
    setMetadata((prevMetadata) => ({
      ...prevMetadata,
      ...updatedFields,
    }));
  };

  const handleSubmit = async () => {
    const formattedMetadata = {
      ...metadata,
      date: metadata.date ? new Date(metadata.date).toISOString().split('T')[0] : '',
      startTime: metadata.startTime ? new Date(metadata.startTime).toISOString() : null,
      endTime: metadata.endTime ? new Date(metadata.endTime).toISOString() : null,
    };

    try {
      const response = await fetch('http://localhost:3001/journal-entries', { // Update the endpoint here
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user: user.username,
          text: metadata.text,
          metadata: JSON.stringify(formattedMetadata), // Stringify metadata
        }),
      });
      if (response.ok) {
        console.log('Journal entry saved successfully');
      } else {
        console.error('Failed to save journal entry');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Create Journal Entry</h2>
      <SpeechToText onMetadataChange={handleMetadataChange} />
      <MetadataDisplay metadata={metadata} onMetadataChange={handleMetadataUpdate} />
      <div style={{ textAlign: 'left', marginTop: '20px' }}>
        <h3>Recording Metadata</h3>
        <p>
          <strong>Date:</strong> {metadata.date}<br />
          <strong>Start Time:</strong> {metadata.startTime}<br />
          <strong>End Time:</strong> {metadata.endTime}<br />
          <strong>Duration:</strong> {metadata.duration && `${Math.floor(metadata.duration / 1000)} seconds`}<br />
          <strong>Text:</strong> {metadata.text}<br />
          <strong>Names:</strong> {metadata.names.join(', ')}<br />
          <strong>Keywords:</strong> {metadata.keywords.join(', ')}
        </p>
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default JournalEntry;
