import React, { useEffect, useState } from 'react';
import KeywordAnalysis from '../components/KeywordAnalysis';
import SentimentAnalysis from '../components/SentimentAnalysis';
import MediaAnalysisOverview from '../components/MediaAnalysisOverview';
import EntryTimeline from '../components/EntryTimeline';
import { useUser } from '../UserContext';

const Insights = () => {
  const { user } = useUser();
  const [entries, setEntries] = useState([]);
  const [keywords, setKeywords] = useState([]);
  const [mediaAnalysis, setMediaAnalysis] = useState([]);

  // Default data with more variation
  const defaultEntries = [
    {
      text: "Had a fantastic day at work! Got a promotion, feeling on top of the world. Excited for the future!",
      metadata: { keywords: ["work", "promotion", "excited", "future"] },
      mediaAnalysis: { labels: ["Positive", "Neutral"], texts: ["Overjoyed", "Hopeful"] },
    },
    {
      text: "It's been a tough week. Struggling with personal issues, feeling a bit lost and overwhelmed by everything.",
      metadata: { keywords: ["tough", "personal", "struggling", "overwhelmed"] },
      mediaAnalysis: { labels: ["Negative", "Neutral"], texts: ["Frustrated", "Stressed"] },
    },
    {
      text: "Work was okay today. Not much happened, just the usual. Nothing too exciting or frustrating.",
      metadata: { keywords: ["work", "usual", "nothing", "okay"] },
      mediaAnalysis: { labels: ["Neutral", "Neutral"], texts: ["Indifferent", "Neutral"] },
    },
    {
      text: "Feeling great today! The sun is shining, the weather's perfect, and everything just feels right.",
      metadata: { keywords: ["feeling great", "sun", "weather", "perfect"] },
      mediaAnalysis: { labels: ["Positive", "Neutral"], texts: ["Happy", "Calm"] },
    },
    {
      text: "I had a setback at work. Missed an important deadline, and now I have to work late to catch up.",
      metadata: { keywords: ["setback", "work", "deadline", "catch up"] },
      mediaAnalysis: { labels: ["Negative", "Neutral"], texts: ["Disappointed", "Tired"] },
    },
  ];

  const defaultKeywords = [
    "work", "promotion", "excited", "future", "tough", "personal", "struggling", "overwhelmed",
    "usual", "nothing", "okay", "sun", "weather", "perfect", "setback", "deadline", "catch up"
  ];

  const defaultMediaAnalysis = [
    { labels: ["Positive", "Neutral"], texts: ["Overjoyed", "Hopeful"] },
    { labels: ["Negative", "Neutral"], texts: ["Frustrated", "Stressed"] },
    { labels: ["Neutral", "Neutral"], texts: ["Indifferent", "Neutral"] },
    { labels: ["Positive", "Neutral"], texts: ["Happy", "Calm"] },
    { labels: ["Negative", "Neutral"], texts: ["Disappointed", "Tired"] },
  ];

  // If the user is signed in, fetch their entries, else use default data
  useEffect(() => {
    if (user) {
      const fetchEntries = async () => {
        try {
          const response = await fetch(`http://localhost:3001/journal-entries/${user.username}`);
          if (response.ok) {
            const data = await response.json();
            setEntries(data);
            // Aggregate keywords and media analysis results
            const allKeywords = data.flatMap(entry => entry.metadata.keywords);
            setKeywords(allKeywords);
            const allMediaAnalysis = data.map(entry => entry.mediaAnalysis || { labels: [], texts: [] });
            setMediaAnalysis(allMediaAnalysis);
          }
        } catch (error) {
          console.error('Error fetching journal entries:', error);
        }
      };

      fetchEntries();
    } else {
      // Use default data when the user is not signed in
      setEntries(defaultEntries);
      setKeywords(defaultKeywords);
      setMediaAnalysis(defaultMediaAnalysis);
    }
  }, [user]);

  return (
    <div>
      <h1>Insights</h1>
      <KeywordAnalysis keywords={keywords} />
      <SentimentAnalysis entries={entries} />
      <MediaAnalysisOverview mediaAnalysis={mediaAnalysis} />
      <EntryTimeline entries={entries} />
      {!user && (
        <div>
          <h4>Your data will look something like this:</h4>
          <p>Once you're signed in and start adding journal entries, you'll see insights based on your data here.</p>
        </div>
      )}
    </div>
  );
};

export default Insights;
