import React, { useState, useEffect, useRef } from 'react';

const SpeechToText = () => {
  const [text, setText] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [metadata, setMetadata] = useState({
    startTime: null,
    endTime: null,
    duration: null,
    date: null,
    keywords: [],
    names: [],
  });

  const recognitionRef = useRef(null);

  const createRecognitionInstance = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.continuous = true;
    recognition.interimResults = false;
    recognition.lang = 'en-US';

    recognition.onresult = (event) => {
      const transcript = Array.from(event.results)
        .map((result) => result[0].transcript)
        .join('');
      console.log('Transcript received:', transcript);
      setText((prevText) => prevText + ' ' + transcript);
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      setIsListening(false);
      recognition.stop();
    };

    recognition.onend = () => {
      console.log('Recognition ended. isListening:', isListening);
      if (isListening) {
        recognition.start();
      }
    };

    recognitionRef.current = recognition;
  };

  const startListening = () => {
    console.log('Starting listening');
    createRecognitionInstance();
    const startTime = metadata.startTime ? metadata.startTime : new Date();
    setMetadata((prev) => ({
      ...prev,
      startTime,
      endTime: null,
      duration: null,
      date: new Date(),
    }));
    setIsListening(true);
    recognitionRef.current.start();
  };

  const stopListening = () => {
    console.log('Stopping listening');
    if (recognitionRef.current) {
      const endTime = new Date();
      const duration = endTime - metadata.startTime;
      setMetadata((prev) => ({
        ...prev,
        endTime,
        duration,
      }));
      setIsListening(false);
      recognitionRef.current.stop();
    }
  };

  const extractMetadata = async () => {
    if (!text.trim()) {
      return;
    }
    console.log('Extracting metadata');
    try {
      const response = await fetch('http://localhost:3001/api/extract-metadata', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text }),
      });
  
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
  
      const data = await response.json();
      console.log('Response from backend:', data);
  
      // Extract and parse content from the response
      const content = data.choices && data.choices[0] && data.choices[0].message.content ? data.choices[0].message.content : '';
      if (!content) {
        throw new Error('Invalid response: Content is empty or undefined');
      }
  
      const keywordsMatch = content.match(/Keywords: (.*)/);
      const namesMatch = content.match(/Names: (.*)/);
  
      // Handle cases where there may be missing or empty values
      const keywords = keywordsMatch && keywordsMatch[1].trim() !== '' ? keywordsMatch[1].split(',').map(kw => kw.trim()).filter(kw => kw) : [];
      const names = namesMatch && namesMatch[1].trim() !== '' ? namesMatch[1].split(',').map(name => name.trim()).filter(name => name) : [];
  
      // Conditionally update metadata with keywords and names if they are not empty
      setMetadata((prev) => {
        const updatedMetadata = {
          ...prev,
          keywords: keywords.length > 0 ? keywords : prev.keywords,
          names: names.length > 0 ? names : prev.names,
        };
        console.log('Updated metadata:', updatedMetadata);
        return updatedMetadata;
      });
  
    } catch (error) {
      console.error('Error extracting metadata:', error);
    }
  };
  
  

  const handleTextChange = (event) => {
    setText(event.target.value);
    const endTime = new Date();
    setMetadata((prev) => ({
      ...prev,
      endTime,
    }));
  };

  const formatTime = (time) => {
    return time ? new Date(time).toLocaleTimeString() : 'N/A';
  };

  const formatDate = (time) => {
    return time ? new Date(time).toLocaleDateString() : 'N/A';
  };

  const formatDuration = (duration) => {
    if (!duration) return 'N/A';
    const seconds = Math.floor(duration / 1000);
    return `${seconds} seconds`;
  };

  useEffect(() => {
    console.log('Metadata updated:', metadata);
  }, [metadata]);

  useEffect(() => {
    console.log('isListening state:', isListening);
  }, [isListening]);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', marginTop: '50px' }}>
      <div style={{ flex: 1, textAlign: 'center', marginRight: '20px' }}>
        <h1>Speech to Text</h1>
        <div style={{ margin: '20px 0' }}>
          <button onClick={isListening ? stopListening : startListening}>
            {isListening ? 'Stop Listening' : 'Start Listening'}
          </button>
        </div>
        <textarea
          value={text}
          rows="10"
          cols="50"
          onChange={handleTextChange}
          placeholder="Your speech or text will appear here..."
        />
        <div style={{ margin: '20px 0' }}>
          <button onClick={extractMetadata}>
            Extract Metadata
          </button>
        </div>
      </div>
      <div>
        <h2>Recording Metadata</h2>
        <p><strong>Date:</strong> {formatDate(metadata.startTime)}</p>
        <p><strong>Start Time:</strong> {formatTime(metadata.startTime)}</p>
        <p><strong>End Time:</strong> {formatTime(metadata.endTime)}</p>
        <p><strong>Duration:</strong> {formatDuration(metadata.duration)}</p>
        <p><strong>Names:</strong> {metadata.names.length > 0 ? metadata.names.join(', ') : 'N/A'}</p>
        <p><strong>Keywords:</strong> {metadata.keywords.length > 0 ? metadata.keywords.join(', ') : 'N/A'}</p>
      </div>
    </div>
  );
};

export default SpeechToText;
