import React, { useState, useEffect } from 'react';
import { useUser } from '../UserContext';
import JournalEntryTable from '../components/JournalEntryTable';
import KeywordAnalysis from '../components/KeywordAnalysis'; // Import KeywordAnalysis
import '../styles/Dashboard.css'; // Import the CSS file

const Dashboard = () => {
  const { user } = useUser();
  console.log('User data:', user);

  const [journalEntries, setJournalEntries] = useState([]);

  useEffect(() => {
    const fetchJournalEntries = async () => {
      if (user && user.username) {
        try {
          const response = await fetch(`http://localhost:3001/journal-entries/${user.username}`);
          if (!response.ok) {
            throw new Error('Failed to fetch journal entries');
          }
          const data = await response.json();
          console.log('Journal Entries from Backend:', data);
          setJournalEntries(data);
        } catch (error) {
          console.error(error.message);
        }
      }
    };

    fetchJournalEntries();
  }, [user]);

  return (
    <div className="dashboard-container">
      {user ? (
        <>
          <h1 className="dashboard-title">Welcome, {user.username}</h1>
          <div className="card-container">
            {/* KeywordAnalysis will fetch its own data based on user context */}
            <KeywordAnalysis />
            <JournalEntryTable journalEntries={journalEntries} />
          </div>
        </>
      ) : (
        <div className="no-user-message">
          <p>Your journal entries will appear here, searchable based on autogenerated metadata from your media.</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
